SELECT ZONE_ID, DATE, (SELECT NAME FROM ZONE WHERE ID=ZONE_ID) AS ZONENAME, AVERAGE_CO2 AVERAGECO2, AVERAGE_TEMPERATURE AVERAGETEMPERATURE, AVERAGE_HUMIDITY AVERAGEHUMIDITY, AVERAGE_DUST AVERAGEDUST FROM ZONE_AIR_HOUR_STATISTICS WHERE 1=1
AND DATE BETWEEN #{startDate}::timestamp AND #{endDate}::timestamp
AND ZONE_ID = #{zoneId}

SELECT ZONE_ID, DATE, (SELECT NAME FROM ZONE WHERE ID=ZONE_ID) AS ZONENAME, AVERAGE_CO2 AVERAGECO2, AVERAGE_TEMPERATURE AVERAGETEMPERATURE, AVERAGE_HUMIDITY AVERAGEHUMIDITY, AVERAGE_DUST AVERAGEDUST FROM ZONE_AIR_DAY_STATISTICS WHERE 1=1
